#!/usr/bin/env python3
import sys
import math

#problem 12: take DNA sequence and count G in given substring regardless of case

sequence = ""

lowersequence = sequence.lower()
start = int(sys.argv[1])-1
end = int(sys.argv[2])

subseq = lowersequence[start:end]
countG = subseq.count("g")

print("Number of Gs in substring:", countG)
